    @Test
    public void testGetOutput() {
        String actual = Solution.getOutput(new char[][]{
                "1112".toCharArray(),
                "1912".toCharArray(),
                "1892".toCharArray(),
                "1234".toCharArray()
        });
        String expected =
                "1112\n" +
                "1X12\n" +
                "18X2\n" +
                "1234";
        Assert.assertEquals(expected, actual);
    }

